<?phpnamespace App\Http\Controllers\Api\V2;use App\Http\Resources\V2\BlogCollection;use App\Http\Controllers\Controller;use App\Models\Blog;use Illuminate\Http\Request;class BlogController extends Controller{    /**     * Obtener todos los blogs     *     * @return \Illuminate\Http\Response     */    public function index()    {        $blogs = Blog::all();        return new BlogCollection($blogs);           }    /**     * Obtener un blog específico por ID     *     * @param int $id     * @return \Illuminate\Http\Response     */    public function show($id)    {        $blog = Blog::find($id);        if (!$blog) {            return response()->json([                'success' => false,                'message' => 'Blog not found'            ], 404);        }        return response()->json([            'success' => true,            'data' => $blog,            'message' => 'Blog retrieved successfully'        ]);    }    /**     * Obtener blogs por categoría     *     * @param int $categoryId     * @return \Illuminate\Http\Response     */    public function getByCategory($categoryId)    {        $blogs = Blog::where('category_id', $categoryId)->get();       return new BlogCollection($blogs);    }    /**     * Obtener blogs activos (status = 1)     *     * @return \Illuminate\Http\Response     */    public function getActiveBlogs()    {        $blogs = Blog::where('status', 1)->get();       return new BlogCollection($blogs);    }}