<?phpnamespace App\Http\Controllers\Api\V2;use App\Http\Resources\V2\BusinessSettingCollection;use App\Models\BusinessSetting;use Illuminate\Http\Request;class BusinessSettingController extends Controller {    public function index(Request $request) {        if($request->has('type') && !empty($request->input('type'))){            $setting = BusinessSetting::where('type', $request->input('type'))->first();            return response()->json([                'result' => true,                'message' => 'success',                'data' => $setting->value            ]);        }else{            $settings = BusinessSetting::orderBy('type')->get();            return new BusinessSettingCollection($settings);        }    }}