<?phpnamespace App\Http\Controllers\Api\V2;use App\Models\Order;use Illuminate\Http\Request;class OfflinePaymentController extends Controller {    public function submit(Request $request) {        $order = Order::findOrFail($request->order_id);        $data = $request->only('name', 'amount', 'trx_id', 'photo');        if (in_array(null, $data, true)) {            return response()->json([                'result' => false,                'message' => translate('Something went wrong')            ]);        }        $order->update([            'manual_payment_data' => json_encode($data),            'payment_type' => $request->payment_option,            'payment_status' => 'Submitted',            'manual_payment' => 1        ]);        return response()->json([            'result' => true,            'message' => translate('Submitted Successfully')        ]);    }}