<?phpnamespace App\Http\Controllers\Api\V2;use App\Models\CombinedOrder;use Illuminate\Http\Request;class PaymentController extends Controller {    public function cashOnDelivery(Request $request) {        $order = new OrderController;        return $order->store($request);    }    public function manualPayment(Request $request) {        $order = new OrderController;        return $order->store($request);    }    public function wireTransfer(Request $request){        $jsonResponse = (new OrderController)->store($request);        $order = json_decode($jsonResponse->content(), true);        $combined_order = CombinedOrder::findOrFail($order['combined_order_id']);        $manual_payment_data = [            'name' => $request->payment_type,            'amount' => $combined_order->grand_total,            'trx_id' => $request->trx_id,            'photo' => $request->photo        ];        foreach ($combined_order->orders as $order) {            $order->update([                'manual_payment' => true,                'manual_payment_data' => json_encode($manual_payment_data)            ]);        }        return $jsonResponse;    }}