<?phpnamespace App\Http\Controllers;use App\Models\Shop;use App\Models\User;use Illuminate\Http\Request;class EmailController extends Controller{    public function check(Request $request)    {        $exists = User::where('email', $request->input('email'))            //->where('user_type', $request->user_type)            ->exists();        $user = User::where('email', $request->input('email'))->first();        return response()->json(['exists' => $exists, 'user' => $user]);    }    public function checkRnc(Request $request)    {        $exists = Shop::where('rnc', $request->input('rnc'))            ->exists();        return response()->json(['exists' => $exists]);    }    public function checkCedula(Request $request)    {        $exists = User::where('cedula', $request->input('cedula'))            ->whereNotNull('email_verified_at')            ->whereNotNull('phone_verified_at')            ->whereNotNull('correo_verified_at')            ->exists();        $user = User::where('cedula', $request->input('cedula'))->get();        /*$exists = User::where('email', $request->input('email'))            ->whereNotNull('email_verified_at')            ->whereNotNull('phone_verified_at')            ->whereNotNull('correo_verified_at')            ->exists();*/        return response()->json(['exists' => $exists]);    }    public function checkValidate($email)    {        dd($email);    }}