<?phpnamespace App\Http\Controllers;use Auth;class ShopViewsController extends Controller {    /* Paso 1 - Elegir tipo de cuenta */    public function account_type() {        return view('frontend.registro-comercio.views.account-type');    }    /* Paso 3 - Verificacion de correo */    public function verification_email() {        $user = Auth::user(); // Obtener el usuario autenticado        return view('frontend.registro-comercio.views.email-verification')->with('user', $user);    }    /* Paso 4 - Cuenta creada correctamente */    public function signup_complete() {        $user = Auth::user(); // Obtener el usuario autenticado        return view('frontend.registro-comercio.views.signup-complete')->with('user', $user);    }}