<?phpnamespace App\Http\Controllers;use App\Models\User;use Illuminate\Http\Request;use Illuminate\Support\Facades\Log;class EmailController extends Controller {    public function check(Request $request) {        $exists = User::where('email', $request->input('email'))                        ->whereNotNull('email_verified_at')                        ->whereNotNull('phone_verified_at')                        ->whereNotNull('correo_verified_at')                        ->exists();        return response()->json(['exists' => $exists]);    }}