<?phpnamespace App\Http\Middleware;use Closure;use Illuminate\Http\Request;class WorkshopMiddleware {    /**     * Handle an incoming request.     *     * @param \Illuminate\Http\Request $request     * @param \Closure(\Illuminate\Http\Request): (\Illuminate\Http\Response|\Illuminate\Http\RedirectResponse) $next     * @return \Illuminate\Http\Response|\Illuminate\Http\RedirectResponse     */    public function handle(Request $request, Closure $next) {        if ($request->user() && $request->user()->add_user_type === 'workshop' && $request->user()->user_type === 'seller') {            return $next($request);        }        abort(403, 'Unauthorized');    }}