<?phpnamespace App\Http\Resources\V2;use Illuminate\Http\Resources\Json\ResourceCollection;class BlogCollection extends ResourceCollection{    /**     * Transform the resource collection into an array.     *     * @param  \Illuminate\Http\Request  $request     * @return array     */    public function toArray($request)    {        return [            'data' => $this->collection->map(function($data) {                return [                    'id' => $data->id,                    'category_id' => $data->category_id,                    'title' => $data->title,                    'slug' => $data->slug,                    'short_description' => $data->short_description,                    'description' => $data->description,                    'banner' => $data->banner,                    'status' => $data->status,                    'created_at' => $data->created_at,                    'updated_at' => $data->updated_at                ];            })        ];    }    /**     * Get additional data that should be returned with the resource array.     *     * @param  \Illuminate\Http\Request  $request     * @return array     */    public function with($request)    {        return [            'success' => true,            'status' => 200,            'message' => 'Blogs retrieved successfully'        ];    }}