<?phpnamespace App\Mail;use App\Models\User;use Illuminate\Bus\Queueable;use Illuminate\Contracts\Queue\ShouldQueue;use Illuminate\Mail\Mailable;use Illuminate\Queue\SerializesModels;use Illuminate\Support\Str;class AcceptWorkshopProposalEmail extends Mailable implements ShouldQueue {    use Queueable, SerializesModels;    public $nombreCliente;    public $idTaller;    public $token;    public function __construct($nombreCliente, $idTaller) {        $this->$nombreCliente = $nombreCliente;        $this->idTaller = $idTaller;        $this->token = Str::random(60);        $usuario = User::find($idTaller);        $usuario->updateOrCreate(['id' => $idTaller], ['login_token' => $this->token]);    }    public function build() {        return $this->view('emails.accept_workshop_proposal')->with('token', $this->token);    }}