<?phpnamespace App\Mail;use Illuminate\Bus\Queueable;use Illuminate\Contracts\Queue\ShouldQueue;use Illuminate\Mail\Mailable;use Illuminate\Queue\SerializesModels;class WorkshopEmail extends Mailable implements ShouldQueue {    use Queueable, SerializesModels;    protected $token;    public function __construct($token) {        $this->token = $token;    }    public function build() {        return $this->view('emails.workshop')            ->subject('Solicitud de servicio activo')            ->with([                'token' => $this->token,            ]);    }}