<?phpnamespace App\Models;use Illuminate\Database\Eloquent\Factories\HasFactory;use Illuminate\Database\Eloquent\Model;use Illuminate\Database\Eloquent\SoftDeletes;class Blog extends Model{    use HasFactory, SoftDeletes;    protected $table = 'blogs';        protected $fillable = [        'category_id',        'title',        'slug',        'short_description',        'description',        'banner',        'meta_title',        'meta_img',        'meta_description',        'meta_keywords',        'status'    ];        protected $dates = [        'created_at',        'updated_at',        'deleted_at'    ];        // Relación con la categoría    public function category()    {        return $this->belongsTo(BlogCategory::class, 'category_id');    }}