<?phpnamespace App\Models;use Illuminate\Database\Eloquent\Model;class SellerPackagePayment extends Model {    protected $fillable = [        'user_id',        'seller_package_id',        'payment_method',        'payment_details',        'approval',        'offline_payment',        'reciept'    ];    protected $attributes = [        'approval' => 0,        'offline_payment' => 0    ];    public function user() {        return $this->belongsTo(User::class);    }    public function seller_package() {        return $this->belongsTo(SellerPackage::class);    }}