<?phpnamespace App\Models;use Illuminate\Database\Eloquent\Model;class Tax extends Model {    protected $table = 'taxes';    protected $primaryKey = 'id';    protected $fillable = [        'name',        'tax_status'    ];    protected $attributes = [        'tax_status' => 1    ];    public function product_taxes() {        return $this->hasMany(ProductTax::class);    }}