blecer el valor de una cookie        function setCookie(name, value, days) {            var expires = new Date();            expires.setTime(expires.getTime() + (days * 24 * 60 * 60 * 1000));            document.cookie = name + "=" + value + ";expires=" + expires.toUTCString() + ";path=/";        }    });</script>