ype="text/javascript">        $(document).ready(function () {            const submitButton = $("#submit-button-info");            function toggleSubmitButton() {                if ($(".address-card.selected").length > 0) {                    submitButton.prop("disabled", false);                } else {                    submitButton.prop("disabled", true);                }            }            toggleSubmitButton();            $(document).on('click', '.address-card', function () {                const parentCard = $(this).closest(".address-card");                if (parentCard.hasClass('selected')) {                    toggleSubmitButton();                } else {                    toggleSubmitButton();                }            });            submitButton.on('click', function () {                window.location.href = "{{ route('checkout.store_shipping_infostore') }}";            });        });        url = "{{ config('app.url') }}"        function edit_address(id, longitude, latitude) {            $('#edit-address-modal_' + id).modal('show');            showMapedit('map_canvas_' + id, longitude, latitude);            $('#submit-edit-address-' + id).attr('data-id', id);        }        $('[id^=submit-edit-address-]').on('click', function (event) {            event.preventDefault();            let id = $(this).data('id');            let url = "{{ route('addresses.update', '__id__') }}".replace('__id__', id);            let formData = $('#edit-address-form').serialize();            $.ajax({                headers: {                    'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')                },                method: "PUT",                url: url,                data: formData,                success: function (data, textStatus, jqXHR) {                    if (data.state === 'success') {                        Swal({                            type: 'success',                            title: 'Bien',                            text: data.msg,                            timer: 6000                        }).then(() => {                            location.reload();                        })                    } else {                        Swal({                            type: 'error',                            title: 'Oops...',                            text: data.msg,                            timer: 6000                        })                    }                },                error: function (xhr, status, error) {                    console.error(xhr.responseText);                }            })        });        $("#add-new-address").on('click', function (e) {            e.preventDefault();            $('#new-address-modal').modal('show');            showMapnew("new-map_canvas");        });        $('#submit-new-address').on('click', function (event) {            event.preventDefault();            var url = "{{ route('addresses.store') }}";            var formData = $('#form-address').serialize();            $.ajax({                headers: {                    'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')                },                url: url,                type: 'POST',                data: formData,                success: function (response) {                    $('#new-address-modal').modal('hide');                    if (response.state === 'success') {                        Swal.fire({                            type: 'success',                            title: 'Bien',                            text: response.msg,                            timer: 6000                        }).then(() => {                            location.reload();                        })                    } else {                        Swal.fire({                            type: 'error',                            title: 'Oops...',                            text: response.msg,                            timer: 6000                        })                    }                },                error: function (xhr, status, error) {                    console.error(xhr.responseText);                }            });        });    </script>    <script src="{{ asset('public/assets/js/utilities/Maps/main.js') }}" type="text/javascript" defer></script>    <script>        function addPlusOneToPhoneInputs() {            const inputValue = this.value;            if (!inputValue || !inputValue.startsWith('+1')) {                this.value = '+1';            }            if (inputValue && inputValue.length <= 2) {                this.value = '+1';            }        }        // Try adding +1 on DOM loaded        document.addEventListener('DOMContentLoaded', function () {            const phoneunoInput = document.getElementById('phoneuno');            const inputs = $("[id^='edit-address-modal_'] input.phone");            phoneunoInput.addEventListener('input', addPlusOneToPhoneInputs);            inputs.each(function () {                $(this).on('input', addPlusOneToPhoneInputs);                if ($(this).val().length <= 3) {                    $(this).val('+1');                }            });            if (phoneunoInput.value.length <= 3) {                phoneunoInput.value = '+1';            }        });    </script>    <script>        function validatePhone(input, errorSpan) {            const inputValue = input.val().trim().replace(/\s/g, ''); // Eliminar espacios en blanco            // Validaci√≥n            if (inputValue.length === 0) {                errorSpan.text('El campo n√∫mero m√≥vil es obligatorio.');            } else if (!/^[\d+]+$/.test(inputValue)) {                errorSpan.text('Solo se permiten n√∫meros y el s√≠mbolo "+".');            } else if (!/^\+1\d{10}$/.test(inputValue)) {                errorSpan.text('El n√∫mero m√≥vil debe tener el formato +1 seguido de 10 n√∫meros.');            } else {                errorSpan.text('');            }            // Establecer estilo de input basado en si hay un error o no            input.css({                'border': errorSpan.text() ? '1px solid #dc3545' : '1px solid #198754',                'box-shadow': errorSpan.text() ? '0 0 5px #dc3545' : '0 0 5px #198754'            });        }        $(document).ready(function () {            $('#phoneuno').on('input', function () {                validatePhone($(this), $('#phoneError'));            });            $('[id^="phonedos_"]').on('input', function () {                validatePhone($(this), $(this).siblings('#phoneErrordos'));            });        });    </script>@endsection