              })                    } else {                        Swal({                            type: 'error',                            title: 'Oops...',                            text: data.msg,                            timer: 6000                        })                    }                },                error: function (xhr, status, error) {                    console.error(xhr.responseText);                }            })        });        $('.new-email-verification').on('click', function() {            $(this).find('.loading').removeClass('d-none');            $(this).find('.default').addClass('d-none');            var email = $("input[name=email]").val();            $.post('{{ route('user.new.verify') }}', {                _token: '{{ csrf_token() }}',                email: email            }, function(data) {                data = JSON.parse(data);                $('.default').removeClass('d-none');                $('.loading').addClass('d-none');                if (data.status == 2)                    AIZ.plugins.notify('warning', data.message);                else if (data.status == 1)                    AIZ.plugins.notify('success', data.message);                else                    AIZ.plugins.notify('danger', data.message);            });        });    </script>    @if (get_setting('google_map') == 1)        @include('frontend.partials.google_map')    @endif@endsection