      }, // Disable ordering on column 0 (checkbox)                ],            });        });    </script>    <script>        $(document).ready(function () {            $('#workshop_Service_Proposals').DataTable({                responsive: true,                "autoWidth": false,                language: {                    searchPlaceholder: "Buscar...",                    search: "",                    url: "//cdn.datatables.net/plug-ins/1.13.1/i18n/es-ES.json",                },                'order': [],                'columnDefs': [{                    orderable: false,                    targets: 0                }, // Disable ordering on column 0 (checkbox)                ],            });        });    </script>    <script>        $(document).ready(function () {            $('.acceptServiceButton').on('click', function () {                var workshopServiceId = $(this).data('workshop-service-id');                $.ajax({                    url: '{{ route('frontend.user.workshop_request.update', '') }}/' + workshopServiceId,                    type: 'PUT',                    data: {_token: '{{ csrf_token() }}'},                    success: function (response) {                        // Si la solicitud se realizó correctamente, guardar el estado en el almacenamiento local                        localStorage.setItem('acceptServiceButtonClicked', workshopServiceId);                        // Recargar la página                        location.reload();                    },                    error: function () {                        // Si hay un error, muestra un mensaje al usuario                        alert('Error al aceptar el servicio');                    }                });            });            // Verificar si se hizo clic en el botón "Aceptar servicio" antes de recargar la página            var workshopServiceId = localStorage.getItem('acceptServiceButtonClicked');            if (workshopServiceId) {                // Eliminar el estado del botón del almacenamiento local                localStorage.removeItem('acceptServiceButtonClicked');                // Abrir el modal correspondiente al botón "Aceptar servicio" actual                $('#proposal_' + workshopServiceId).modal('show');            }        });    </script>    {{-- <script>        $(document).ready(function() {          // Verificar si se hizo clic en el botón antes de recargar la página          var isButtonClicked = localStorage.getItem('solicitarOfertasButtonClicked');          if (isButtonClicked) {            // Eliminar el estado del botón del almacenamiento local            localStorage.removeItem('solicitarOfertasButtonClicked');            // Obtener el ID del modal correspondiente al botón            var targetModal = $('#solicitarOfertasButton').attr('data-target');            // Abrir el modal correspondiente al botón            $(targetModal).modal('show');          }          // Agregar el evento de clic al botón "Solicitar ofertas de instalación de servicio para este producto"          $(document).on('click', '#solicitarOfertasButton', function() {            // Guardar el estado del botón en el almacenamiento local            localStorage.setItem('solicitarOfertasButtonClicked', true);          });        });    </script> --}}    <script>        // Obtén todos los elementos select dentro de los modales        const selectElements = document.querySelectorAll('.modal .nombre_nueva_clase');        // Agrega un event listener a cada elemento select dentro de los modales        selectElements.forEach(function (selectElement) {            selectElement.addEventListener('change', function (event) {                // Verifica si el evento proviene del elemento select                if (event.target.classList.contains('nombre_nueva_clase')) {                    // Obtén el valor seleccionado                    const selectedValue = event.target.value;                    // Obtén el ID del modal                    const modalId = event.target.closest('.modal').id;                    // Obtén los elementos de los cards dentro del modal actual                    const onlinePaymentCard = document.querySelector(`#${modalId} .card#onlinePaymentCard`);                    const offlinePaymentCard = document.querySelector(`#${modalId} .card#offlinePaymentCard`);                    // Muestra u oculta los cards según la selección                    if (selectedValue === 'online') {                        onlinePaymentCard.style.display = 'block';                        offlinePaymentCard.style.display = 'none';                    } else if (selectedValue === 'offline') {                        onlinePaymentCard.style.display = 'none';                        offlinePaymentCard.style.display = 'block';                    }                }            });        });    </script>    <script type="text/javascript">        function select_payment_type(id) {            $('input[name=package_id]').val(id);            $('#select_payment_type_modal').modal('show');        }        function payment_type(type) {            var package_id = $('#package_id').val();            if (type == 'online') {                $("#select_type_cancel").click();                show_price_modal(package_id);            } else if (type == 'offline') {                $("#select_type_cancel").click();                $.post('{{ route('seller.offline_seller_package_purchase_modal') }}', {                    _token: '{{ csrf_token() }}',                    package_id: package_id                }, function (data) {                    $('#offline_seller_package_purchase_modal_body').html(data);                    $('#offline_seller_package_purchase_modal').modal('show');                });            }        }        function show_price_modal(id) {            $('input[name=seller_package_id]').val(id);            $('#price_modal').modal('show');        }        function get_free_package(id) {            $('input[name=seller_package_id]').val(id);            $('#package_payment_form').submit();        }    </script>@endsection