() {            $('#email').val('seller@example.com');            $('#password').val('123456');        }        function autoFillCustomer() {            $('#email').val('customer@example.com');            $('#password').val('123456');        }        function autoFillDeliveryBoy() {            $('#email').val('deliveryboy@example.com');            $('#password').val('123456');        }    </script>    <script>        $(document).ready(function() {            // Obtener el estado almacenado en la cookie            var rememberMe = getCookie('rememberMe');            // Establecer el estado del checkbox según el valor de la cookie            if (rememberMe === 'true') {                $('input[name="remember"]').prop('checked', true);            }            // Manejar el evento de cambio del checkbox            $('input[name="remember"]').change(function() {                // Obtener el estado actual del checkbox                var isChecked = $(this).is(':checked');                // Almacenar el estado en la cookie                setCookie('rememberMe', isChecked, 365);            });            // Función para obtener el valor de una cookie            function getCookie(name) {                var value = "; " + document.cookie;                var parts = value.split("; " + name + "=");                if (parts.length === 2) {                    return parts.pop().split(";").shift();                }            }            // Función para establecer el valor de una cookie            function setCookie(name, value, days) {                var expires = new Date();                expires.setTime(expires.getTime() + (days * 24 * 60 * 60 * 1000));                document.cookie = name + "=" + value + ";expires=" + expires.toUTCString() + ";path=/";            }        });    </script>@endsection